import React, { Component } from "react";
import { Route } from "react-router-dom";
import {
  getAllCategories,
  getAllDances,
  postDance
} from "../services/api-helper";
import Login from "./Login";
import Register from "./Register";
import Categories from "./Categories";
import Category from "./Category";
import OneDance from "./OneDance";
import CreateDance from "./CreateDance";
import AllDances from "./AllDances";
export default class Main extends Component {
  state = {
    categories: [],
    dances: []
  };

  componentDidMount() {
    this.readAllCategories();
    this.readAllDances();
  }
  readAllCategories = async () => {
    const categories = await getAllCategories();
    this.setState({ categories });
  };

  readAllDances = async () => {
    const dances = await getAllDances();
    this.setState({ dances });
  };

  handleDanceSubmit = async (danceData) => {
    const newDance = await postDance(danceData);
    this.setState(prevState => ({
      dances: [...prevState.dances, newDance]
    }));
  };

  handleDanceUpdate = async (id, danceData) => {
    const updatedDance = await putDance(id, danceData);
    this.setState(prevState => ({
      dances: prevState.dances.map(dance => {
        return dance.id === id ? updatedDance : dance
      })
    }))
  }

  // handleFoodDelete = async (id) => {
  //   await destroyFood(id);
  //   this.setState(prevState => ({
  //     foods: prevState.foods.filter(food => {
  //       return food.id !== id
  //     })
  //   }))
  // }

  render() {
    return (
      <main>
        <Route
          path="/login"
          render={props => (
            <Login {...props} handleLogin={this.props.handleLogin} />
          )}
        />
        <Route
          path="/register"
          render={props => (
            <Register {...props} handleRegister={this.props.handleRegister} />
          )}
        />

        <Route
          exact
          path="/categories"
          render={props => (
            <Categories
              {...props}
              categories={this.state.categories}
              dances={this.state.dances}
            />
          )}
        />

<Route
          exact
          path="/dances"
          render={props => (
            <AllDances
              {...props}

              dances={this.state.dances}
            />
          )}
        />

        <Route
          exact
          path="/categories/:id"
          render={props => {
            const { id } = props.match.params;
            return (
              <Category
                categoryId={id}
                // categories={this.state.categories}
                // dances={this.state.dances}
              />
            );
          }}
        />

        <Route
          exact
          path="/dances/:id"
          render={props => {
            const { id } = props.match.params;
            return (
              <OneDance
                danceId={id}
                // categories={this.state.categories}
                // dances={this.state.dances}
              />
            );
          }}
        />

        <Route
          path="/dances/new"
          render={props => (
            <CreateDance
              {...props}
              handleDanceSubmit={this.handleDanceSubmit}
            />
          )}
        />

<Route path='/dances/:id/edit' render={(props) => {
          const { id } = props.match.params
          return <UpdateDance
            {...props}
            handleDanceUpdate={this.handleDanceUpdate}
            danceId={id}
          />
        }} />
      </main>
    );
  }
}
