import React, { Component } from "react";
import { Route } from "react-router-dom";
import {
  getAllCategories,
  getAllDances,
  getOneCategory
} from "../services/api-helper";
import Login from "./Login";
import Register from "./Register";
import Categories from "./Categories";
import Category from "./Category";

export default class Main extends Component {
  state = {
    categories: [],
    dances: [],
    category: []
  };

  componentDidMount() {
    this.readAllCategories();
    this.readAllDances();
  }
  readAllCategories = async () => {
    const categories = await getAllCategories();
    this.setState({ categories });
  };

  readAllDances = async () => {
    const dances = await getAllDances();
    this.setState({ dances });
  };

  readOneCategory = async id => {
    const category = await getOneCategory();
    this.setState({ category });
  };
  render() {
    return (
      <main>
        <Route
          path="/login"
          render={props => (
            <Login {...props} handleLogin={this.props.handleLogin} />
          )}
        />
        <Route
          path="/register"
          render={props => (
            <Register {...props} handleRegister={this.props.handleRegister} />
          )}
        />

        <Route
          exact
          path="/categories"
          render={props => (
            <Categories {...props} categories={this.state.categories} />
          )}
        />

        <Route
          exact
          path="/categories/:id"
          render={props => (
            <Category {...props} categories={this.state.categories} />
          )}
        />
      </main>
    );
  }
}
